\COPY (SELECT staza.id,staza.naziv,staza.oznaka_drzave,datum_otvorenja,podloga,duljina_km,broj_zavoja,url,kapacitet_gledatelja,vrijeme AS rekord_vrijeme,vozilo AS rekord_vozilo,ime AS rekord_vozac_ime, prezime AS rekord_vozac_prezime,datum_rodenja AS rekord_vozac_datum_rodenja, vozac.oznaka_drzave AS rekord_vozac_oznaka_drzave,natjecanje.naziv AS natjecanja_naziv,godina AS natjecanja_godina FROM staza FULL JOIN rekord ON staza.rekord_id = rekord.id FULL JOIN vozac ON vozac_id = vozac.id FULL JOIN odrzavaSe ON staza.id = odrzavaSe.staza_id FULL JOIN natjecanje ON odrzavaSe.natjecanje_id = natjecanje.id) TO 'data/trkace_staze_po_svijetu.csv' DELIMITER ',' CSV HEADER;